mapper.select:
SELECT * FROM member
WHERE member_no IN (
    SELECT member_no_follower
    FROM relation
    WHERE member_no_following = (
        SELECT member_no
        FROM member
        WHERE member_no = #{member_no}
    )
)

SELECT * FROM member
WHERE member_no IN (
    SELECT member_no_following
    FROM relation
    WHERE member_no_follower = (
        SELECT member_no
        FROM member
        WHERE member_no = #{member_no}
    )
)
